import Joi from 'joi'
import { fromEnv } from '#shared/env'

const shared = {
  setup: {
    /*
     * The setup_token is generated by us,
     * but should be passed in subsequent setup requests
     */
    setup_token: Joi.string().trim().min(16).max(128),
  },
}

/*
 * This describes the schema of request objects
 */
export const requestSchema = {
  setup: {
    morio: Joi.object({
      nodes: Joi.array()
        .items(Joi.string())
        .min(fromEnv('MORIO_CONFIG_NODES_MIN'))
        .max(fromEnv('MORIO_CONFIG_NODES_MAX'))
        .unique()
        .required(),
    }),
    jwtkey: Joi.object({
      setup_token: shared.setup.setup_token,
    }),
    password: Joi.object({
      setup_token: shared.setup.setup_token,
      /*
       * Bytes controls the length of the generated password
       */
      bytes: Joi.number()
        .min(fromEnv('MORIO_CRYPTO_SECRET_MIN'))
        .max(fromEnv('MORIO_CRYPTO_SECRET_MAX'))
        .default(fromEnv('MORIO_CRYPTO_SECRET_DFLT')),
    }),
    keypair: Joi.object({
      setup_token: shared.setup.setup_token,
      /*
       * Passphrase is used to encrypt the private key
       */
      passphrase: Joi.string()
        .min(fromEnv('MORIO_CRYPTO_SECRET_MIN'))
        .max(fromEnv('MORIO_CRYPTO_SECRET_MAX'))
        .required(),
    }),
  },
  validate: {
    configuration: Joi.object({
      config: Joi.object(),
    }),
  },
}

/*
 * This describes the schema of response objects
 */
export const responseSchema = {
  setup: {
    morio: Joi.object({
      setup_token: Joi.string(),
    }),
    jwtkey: Joi.object({
      jwt_key: Joi.string(),
    }),
    password: Joi.object({
      password: Joi.string(),
    }),
    keypair: Joi.object({
      public: Joi.string(),
      private: Joi.string(),
    }),
  },
  status: Joi.object({
    name: Joi.string(),
    about: Joi.string(),
    version: Joi.string(),
    uptime: Joi.string(),
    uptime_seconds: Joi.number(),
    setup: Joi.bool(),
  }),
  validate: {
    config: Joi.object({}),
  },
}

/*
 * This describes the schema of error responses
 */
export const errorsSchema = Joi.object({ errors: Joi.array().items(Joi.string()) })

/*
 * This describes the schema of the morio configuration
 */
export const morioSchema = Joi.object({
  morio: Joi.object({
    display_name: Joi.string().required().min(2).max(255),
    node_count: Joi.number()
      .required()
      .valid(...fromEnv('MORIO_CONFIG_DEPLOYMENT_SIZES')),
    nodes: Joi.array().required().length(Joi.ref('node_count')).items(Joi.string().hostname()),
    cluster_name: Joi.string()
      .hostname()
      .when('node_count', {
        is: Joi.number().max(1),
        then: Joi.forbidden(),
        otherwise: Joi.required(),
      }),
  }),
})
