/*
 * This wrapper helps to keep individual icons DRY
 */
const IconWrapper = ({
  className = 'w-6 h-6',
  stroke = 2,
  children = null,
  fill = false,
}) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    fill={fill ? 'currentColor' : 'none'}
    viewBox="0 0 24 24"
    strokeWidth={stroke}
    stroke="currentColor"
    strokeLinecap="round"
    strokeLinejoin="round"
    className={className + ' icon'}
  >
    {children}
  </svg>
)

