/*
 * This wrapper helps to keep individual icons DRY
 */
export const IconWrapper = ({
  className = 'w-6 h-6',
  stroke = 2,
  children = null,
  fill = false,
}) => (
  <svg
    role="img"
    xmlns="http://www.w3.org/2000/svg"
    fill={fill ? 'currentColor' : 'none'}
    viewBox="0 0 24 24"
    strokeWidth={stroke}
    stroke="currentColor"
    strokeLinecap="round"
    strokeLinejoin="round"
    className={className + ' icon'}
  >
    {children}
  </svg>
)

